import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Link } from 'react-router-dom';
import { useTheme } from '../App';
import { Brain, Zap, Shield, Globe, Play, Star, CheckCircle, ArrowRight, Cpu, Lock, Users, Rocket, Sparkles, Target, Clock, BarChart3, Code, Cloud, Database } from 'lucide-react';

const HeroSection = () => {
  const { colors } = useTheme();
  const [currentSlide, setCurrentSlide] = useState(0);

  const heroStyles = {
    container: {
      position: 'relative',
      minHeight: '100vh',
      display: 'flex',
      alignItems: 'center',
      padding: '80px 2rem 2rem 2rem',
      overflow: 'hidden',
    },
    background: {
      position: 'absolute',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      background: `
        radial-gradient(circle at 15% 50%, ${colors.primary}15 0%, transparent 50%),
        radial-gradient(circle at 85% 30%, ${colors.secondary}10 0%, transparent 50%),
        radial-gradient(circle at 50% 80%, ${colors.accent}08 0%, transparent 50%)
      `,
    },
    floatingOrbs: {
      position: 'absolute',
      width: '100%',
      height: '100%',
    },
    orb: {
      position: 'absolute',
      borderRadius: '50%',
      filter: 'blur(40px)',
      opacity: 0.6,
    },
    content: {
      position: 'relative',
      zIndex: 10,
      maxWidth: '1400px',
      margin: '0 auto',
      width: '100%',
    },
    mainGrid: {
      display: 'grid',
      gridTemplateColumns: '1fr',
      gap: '4rem',
      alignItems: 'center',
    },
    leftColumn: {
      textAlign: 'center',
    },
    badge: {
      display: 'inline-flex',
      alignItems: 'center',
      gap: '0.5rem',
      background: colors.gradient,
      color: 'white',
      padding: '0.75rem 1.5rem',
      borderRadius: '50px',
      fontSize: '0.875rem',
      fontWeight: '600',
      marginBottom: '2rem',
      boxShadow: `0 8px 32px ${colors.primary}40`,
    },
    heading: {
      fontSize: 'clamp(2.5rem, 5vw, 4.5rem)',
      fontWeight: 'bold',
      color: colors.text,
      lineHeight: 1.1,
      marginBottom: '1.5rem',
    },
    highlight: {
      background: colors.gradient,
      WebkitBackgroundClip: 'text',
      WebkitTextFillColor: 'transparent',
      backgroundClip: 'text',
      position: 'relative',
    },
    subheading: {
      fontSize: 'clamp(1rem, 2.5vw, 1.25rem)',
      color: colors.textSecondary,
      lineHeight: 1.6,
      marginBottom: '2.5rem',
      maxWidth: '600px',
      margin: '0 auto',
    },
    ctaSection: {
      display: 'flex',
      gap: '1rem',
      justifyContent: 'center',
      flexWrap: 'wrap',
      marginBottom: '3rem',
    },
    primaryButton: {
      background: colors.gradient,
      color: 'white',
      padding: '1.25rem 2.5rem',
      borderRadius: '16px',
      fontWeight: '600',
      fontSize: '1.1rem',
      textDecoration: 'none',
      border: 'none',
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      gap: '0.75rem',
      transition: 'all 0.3s ease',
      boxShadow: `0 8px 32px ${colors.primary}40`,
    },
    secondaryButton: {
      background: 'transparent',
      color: colors.text,
      padding: '1.25rem 2.5rem',
      borderRadius: '16px',
      fontWeight: '600',
      fontSize: '1.1rem',
      textDecoration: 'none',
      border: `2px solid ${colors.border}`,
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      gap: '0.75rem',
      transition: 'all 0.3s ease',
    },
    statsGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(2, 1fr)',
      gap: '2rem',
      marginBottom: '3rem',
    },
    statCard: {
      background: colors.cardGradient,
      padding: '1.5rem',
      borderRadius: '16px',
      border: `1px solid ${colors.border}`,
      textAlign: 'center',
      backdropFilter: 'blur(10px)',
    },
    statNumber: {
      fontSize: '2rem',
      fontWeight: 'bold',
      background: colors.gradient,
      WebkitBackgroundClip: 'text',
      WebkitTextFillColor: 'transparent',
      backgroundClip: 'text',
      marginBottom: '0.5rem',
    },
    statLabel: {
      color: colors.textSecondary,
      fontSize: '0.9rem',
      fontWeight: '500',
    },
    featureShowcase: {
      background: colors.cardGradient,
      padding: '3rem 2rem',
      borderRadius: '24px',
      border: `1px solid ${colors.border}`,
      marginTop: '3rem',
      backdropFilter: 'blur(20px)',
    },
    showcaseHeader: {
      textAlign: 'center',
      marginBottom: '3rem',
    },
    showcaseTitle: {
      fontSize: '2rem',
      fontWeight: 'bold',
      color: colors.text,
      marginBottom: '1rem',
    },
    showcaseGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))',
      gap: '2rem',
    },
    showcaseCard: {
      background: colors.surface,
      padding: '2rem',
      borderRadius: '16px',
      border: `1px solid ${colors.border}`,
      textAlign: 'center',
      transition: 'all 0.3s ease',
    },
    trustedBy: {
      textAlign: 'center',
      marginTop: '4rem',
      padding: '2rem',
    },
    trustedTitle: {
      color: colors.textSecondary,
      fontSize: '1rem',
      fontWeight: '500',
      marginBottom: '2rem',
    },
    logos: {
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      gap: '3rem',
      flexWrap: 'wrap',
    },
    logoItem: {
      color: colors.textSecondary,
      fontSize: '1.1rem',
      fontWeight: '600',
      opacity: 0.7,
      padding: '0.5rem 1rem',
      border: `1px solid ${colors.border}`,
      borderRadius: '8px',
    },
  };

  const stats = [
    { number: '10x', label: 'Faster Content Creation' },
    { number: '50+', label: 'Languages Supported' },
    { number: '99.9%', label: 'Accuracy Rate' },
    { number: '24/7', label: 'AI Availability' },
  ];

  const showcaseFeatures = [
    {
      icon: Cpu,
      title: 'Multi-Model AI',
      description: 'Combine GPT-4, Claude, and custom models for optimal results',
      color: '#6366f1'
    },
    {
      icon: Lock,
      title: 'Blockchain Proof',
      description: 'Immutable timestamping and content verification on blockchain',
      color: '#8b5cf6'
    },
    {
      icon: Users,
      title: 'Team Collaboration',
      description: 'Real-time editing and approval workflows for teams',
      color: '#ec4899'
    },
    {
      icon: BarChart3,
      title: 'Performance Analytics',
      description: 'AI-powered insights and content performance tracking',
      color: '#10b981'
    },
  ];

  const trustedCompanies = ['MediaCorp', 'EduTech Global', 'AdVenture Pro', 'NewsNetwork', 'LearnSphere', 'ContentFlow'];

  // Floating orbs animation
  const FloatingOrbs = () => (
    <div style={heroStyles.floatingOrbs}>
      <motion.div
        style={{
          ...heroStyles.orb,
          background: colors.primary,
          width: '200px',
          height: '200px',
          top: '20%',
          left: '10%',
        }}
        animate={{
          y: [0, -30, 0],
          x: [0, 20, 0],
        }}
        transition={{
          duration: 8,
          repeat: Infinity,
          ease: "easeInOut"
        }}
      />
      <motion.div
        style={{
          ...heroStyles.orb,
          background: colors.secondary,
          width: '150px',
          height: '150px',
          top: '60%',
          right: '15%',
        }}
        animate={{
          y: [0, 40, 0],
          x: [0, -15, 0],
        }}
        transition={{
          duration: 6,
          repeat: Infinity,
          ease: "easeInOut",
          delay: 1
        }}
      />
      <motion.div
        style={{
          ...heroStyles.orb,
          background: colors.accent,
          width: '120px',
          height: '120px',
          bottom: '20%',
          left: '20%',
        }}
        animate={{
          y: [0, -20, 0],
          x: [0, 10, 0],
        }}
        transition={{
          duration: 7,
          repeat: Infinity,
          ease: "easeInOut",
          delay: 2
        }}
      />
    </div>
  );

  return (
    <div style={heroStyles.container}>
      <div style={heroStyles.background} />
      <FloatingOrbs />
      
      <div style={heroStyles.content}>
        <div style={heroStyles.mainGrid}>
          {/* Main Hero Content */}
          <div style={heroStyles.leftColumn}>
            <motion.div
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8 }}
            >
              <div style={heroStyles.badge}>
                <Sparkles size={16} />
                Next-Gen AI Content Platform
              </div>
            </motion.div>

            <motion.h1 
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.2 }}
              style={heroStyles.heading}
            >
              Transform Your{' '}
              <span style={heroStyles.highlight}>Content Workflow</span>
              <br />
              with AI-Powered Intelligence
            </motion.h1>

            <motion.p 
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.4 }}
              style={heroStyles.subheading}
            >
              Enterprise-grade AI content generation, verification, and optimization. 
              Eliminate bottlenecks, ensure authenticity, and scale your content production 
              across multiple platforms and languages.
            </motion.p>

            <motion.div 
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.6 }}
              style={heroStyles.ctaSection}
            >
              <Link to="/dashboard">
                <motion.button
                  whileHover={{ scale: 1.05, y: -2 }}
                  whileTap={{ scale: 0.95 }}
                  style={heroStyles.primaryButton}
                >
                  <Rocket size={20} />
                  Start Creating Free
                  <ArrowRight size={16} />
                </motion.button>
              </Link>
              <motion.button
                whileHover={{ scale: 1.05, y: -2 }}
                whileTap={{ scale: 0.95 }}
                style={heroStyles.secondaryButton}
              >
                <Play size={20} />
                Watch Demo
              </motion.button>
            </motion.div>

            {/* Stats Grid */}
            <motion.div 
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.8 }}
              style={heroStyles.statsGrid}
            >
              {stats.map((stat, index) => (
                <motion.div
                  key={index}
                  whileHover={{ scale: 1.05, y: -5 }}
                  style={heroStyles.statCard}
                >
                  <div style={heroStyles.statNumber}>{stat.number}</div>
                  <div style={heroStyles.statLabel}>{stat.label}</div>
                </motion.div>
              ))}
            </motion.div>
          </div>
        </div>

        {/* Feature Showcase */}
        <motion.div
          initial={{ opacity: 0, y: 40 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 1 }}
          style={heroStyles.featureShowcase}
        >
          <div style={heroStyles.showcaseHeader}>
            <h2 style={heroStyles.showcaseTitle}>Why Choose VeriCreate?</h2>
            <p style={{ color: colors.textSecondary, fontSize: '1.1rem' }}>
              Advanced features that set us apart from traditional content tools
            </p>
          </div>
          
          <div style={heroStyles.showcaseGrid}>
            {showcaseFeatures.map((feature, index) => (
              <motion.div
                key={index}
                whileHover={{ 
                  scale: 1.05, 
                  y: -8,
                  borderColor: feature.color,
                }}
                style={heroStyles.showcaseCard}
              >
                <div style={{
                  width: '60px',
                  height: '60px',
                  background: `linear-gradient(135deg, ${feature.color}, ${feature.color}dd)`,
                  borderRadius: '16px',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  margin: '0 auto 1.5rem',
                }}>
                  <feature.icon size={28} color="white" />
                </div>
                <h3 style={{ 
                  color: colors.text, 
                  fontSize: '1.25rem',
                  fontWeight: '600',
                  marginBottom: '1rem'
                }}>
                  {feature.title}
                </h3>
                <p style={{ 
                  color: colors.textSecondary, 
                  lineHeight: 1.6,
                  fontSize: '0.95rem'
                }}>
                  {feature.description}
                </p>
              </motion.div>
            ))}
          </div>
        </motion.div>

        {/* Trusted By Section */}
        <motion.div 
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.8, delay: 1.2 }}
          style={heroStyles.trustedBy}
        >
          <div style={heroStyles.trustedTitle}>
            Trusted by innovative teams at
          </div>
          <div style={heroStyles.logos}>
            {trustedCompanies.map((company, index) => (
              <motion.div
                key={index}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: 1.4 + index * 0.1 }}
                whileHover={{ scale: 1.1, opacity: 1 }}
                style={heroStyles.logoItem}
              >
                {company}
              </motion.div>
            ))}
          </div>
        </motion.div>
      </div>
    </div>
  );
};

export default HeroSection;
