import React, { useState } from 'react';
import { motion } from 'framer-motion';
import GenerateCard from '../components/GenerateCard';
import OutputCard from '../components/OutputCard';

const Dashboard = () => {
  const [generatedContent, setGeneratedContent] = useState(null);

  const pageStyles = {
    container: {
      minHeight: '100vh',
      paddingTop: '5rem',
      paddingBottom: '4rem',
    },
    content: {
      maxWidth: '1200px',
      margin: '0 auto',
      padding: '0 1rem',
    },
    header: {
      textAlign: 'center',
      marginBottom: '3rem',
    },
    heading: {
      fontSize: '3rem',
      fontWeight: 'bold',
      color: 'white',
      marginBottom: '1rem',
    },
    subheading: {
      fontSize: '1.25rem',
      color: '#d1d5db',
      maxWidth: '32rem',
      margin: '0 auto',
    },
    grid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(1, 1fr)',
      gap: '2rem',
    },
    emptyState: {
      background: 'linear-gradient(45deg, #667eea, #764ba2, #f093fb)',
      padding: '1px',
      borderRadius: '1rem',
      height: '100%',
    },
    emptyContent: {
      background: 'rgba(255, 255, 255, 0.1)',
      backdropFilter: 'blur(10px)',
      borderRadius: '1rem',
      padding: '2rem',
      border: '1px solid rgba(255, 255, 255, 0.2)',
      height: '100%',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      textAlign: 'center',
    },
  };

  const handleGenerate = (content) => {
    setGeneratedContent(content);
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: -20 }}
      style={pageStyles.container}
    >
      <div style={pageStyles.content}>
        {/* Header */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2 }}
          style={pageStyles.header}
        >
          <h1 style={pageStyles.heading}>
            Content <span style={{ background: 'linear-gradient(45deg, #667eea, #764ba2, #f093fb)', WebkitBackgroundClip: 'text', WebkitTextFillColor: 'transparent' }}>Dashboard</span>
          </h1>
          <p style={pageStyles.subheading}>
            Generate AI-powered content and verify its authenticity with blockchain technology
          </p>
        </motion.div>

        {/* Content Grid */}
        <div style={pageStyles.grid}>
          {/* Generate Card */}
          <GenerateCard onGenerate={handleGenerate} />
          
          {/* Output Card */}
          <div>
            {generatedContent ? (
              <OutputCard content={generatedContent} />
            ) : (
              <div style={pageStyles.emptyState}>
                <div style={pageStyles.emptyContent}>
                  <div>
                    <div style={{ width: '4rem', height: '4rem', background: 'linear-gradient(45deg, #8b5cf6, #ec4899)', borderRadius: '50%', display: 'flex', alignItems: 'center', justifyContent: 'center', margin: '0 auto 1rem' }}>
                      <motion.div
                        animate={{ rotate: 360 }}
                        transition={{ duration: 2, repeat: Infinity, ease: "linear" }}
                        style={{ width: '2rem', height: '2rem', border: '2px solid white', borderTop: '2px solid transparent', borderRadius: '50%' }}
                      />
                    </div>
                    <h3 style={{ fontSize: '1.25rem', fontWeight: 600, color: 'white', marginBottom: '0.5rem' }}>
                      Ready to Create
                    </h3>
                    <p style={{ color: '#d1d5db' }}>
                      Generate content to see the output here
                    </p>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </motion.div>
  );
};

export default Dashboard;
