import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useTheme } from '../App';
import { Wand2, Languages, Image as ImageIcon, Download, RefreshCw, AlertCircle } from 'lucide-react';

const GenerateCard = ({ onGenerate }) => {
  const [title, setTitle] = useState('');
  const [language, setLanguage] = useState('English');
  const [isGenerating, setIsGenerating] = useState(false);
  const [generatedImage, setGeneratedImage] = useState(null);
  const [imagePrompt, setImagePrompt] = useState('');
  const [isGeneratingImage, setIsGeneratingImage] = useState(false);
  const [error, setError] = useState('');
  const { colors } = useTheme();

  const cardStyles = {
    container: {
      background: colors.cardGradient,
      padding: '2rem',
      borderRadius: '24px',
      border: `1px solid ${colors.border}`,
      backdropFilter: 'blur(20px)',
    },
    heading: {
      fontSize: '1.75rem',
      fontWeight: 'bold',
      color: colors.text,
      marginBottom: '2rem',
      display: 'flex',
      alignItems: 'center',
      gap: '0.75rem',
    },
    inputGroup: {
      display: 'flex',
      flexDirection: 'column',
      gap: '1.5rem',
      marginBottom: '2rem',
    },
    input: {
      width: '100%',
      padding: '1rem 1.5rem',
      background: colors.surface,
      border: `1px solid ${colors.border}`,
      borderRadius: '12px',
      color: colors.text,
      fontSize: '1rem',
      outline: 'none',
      transition: 'all 0.3s ease',
    },
    textarea: {
      width: '100%',
      padding: '1rem 1.5rem',
      background: colors.surface,
      border: `1px solid ${colors.border}`,
      borderRadius: '12px',
      color: colors.text,
      fontSize: '1rem',
      minHeight: '100px',
      resize: 'vertical',
      outline: 'none',
      transition: 'all 0.3s ease',
    },
    select: {
      width: '100%',
      padding: '1rem 1.5rem',
      background: colors.surface,
      border: `1px solid ${colors.border}`,
      borderRadius: '12px',
      color: colors.text,
      fontSize: '1rem',
      outline: 'none',
    },
    button: {
      width: '100%',
      padding: '1.25rem 2rem',
      background: colors.gradient,
      color: 'white',
      border: 'none',
      borderRadius: '12px',
      fontSize: '1.1rem',
      fontWeight: '600',
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      gap: '0.75rem',
      transition: 'all 0.3s ease',
      marginBottom: '1rem',
    },
    imageButton: {
      width: '100%',
      padding: '1rem 2rem',
      background: 'transparent',
      color: colors.text,
      border: `2px solid ${colors.border}`,
      borderRadius: '12px',
      fontSize: '1rem',
      fontWeight: '600',
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      gap: '0.5rem',
      transition: 'all 0.3s ease',
      marginBottom: '1.5rem',
    },
    label: {
      display: 'block',
      fontSize: '0.9rem',
      fontWeight: '500',
      color: colors.text,
      marginBottom: '0.5rem',
    },
    imagePreview: {
      width: '100%',
      borderRadius: '12px',
      border: `1px solid ${colors.border}`,
      marginBottom: '1rem',
    },
    error: {
      background: 'rgba(239, 68, 68, 0.1)',
      border: '1px solid rgba(239, 68, 68, 0.3)',
      color: '#ef4444',
      padding: '1rem',
      borderRadius: '12px',
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem',
      marginBottom: '1rem',
    },
    imageActions: {
      display: 'flex',
      gap: '1rem',
      marginBottom: '1.5rem',
    },
    actionButton: {
      flex: 1,
      padding: '0.75rem 1.5rem',
      background: colors.surface,
      border: `1px solid ${colors.border}`,
      borderRadius: '8px',
      color: colors.text,
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      gap: '0.5rem',
      transition: 'all 0.3s ease',
    },
  };

  const languages = ['English', 'Hindi', 'Spanish', 'French', 'German', 'Japanese', 'Chinese', 'Arabic', 'Portuguese'];

  // Stable Diffusion API Integration
  const generateImageWithStableDiffusion = async (prompt) => {
    const STABLE_DIFFUSION_API_KEY = process.env.REACT_APP_STABLE_DIFFUSION_API_KEY;
    const API_URL = 'https://api.stability.ai/v1/generation/stable-diffusion-v1-6/text-to-image';

    if (!STABLE_DIFFUSION_API_KEY) {
      throw new Error('Stable Diffusion API key not configured. Please add REACT_APP_STABLE_DIFFUSION_API_KEY to your environment variables.');
    }

    try {
      const response = await fetch(API_URL, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${STABLE_DIFFUSION_API_KEY}`,
          'Accept': 'application/json',
        },
        body: JSON.stringify({
          text_prompts: [{ text: prompt }],
          cfg_scale: 7,
          height: 512,
          width: 512,
          steps: 30,
          samples: 1,
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to generate image');
      }

      const data = await response.json();
      
      if (data.artifacts && data.artifacts.length > 0) {
        // Convert base64 image to blob URL for display
        const base64Image = data.artifacts[0].base64;
        const blob = base64ToBlob(base64Image, 'image/png');
        return URL.createObjectURL(blob);
      } else {
        throw new Error('No image generated');
      }
    } catch (error) {
      console.error('Stable Diffusion API Error:', error);
      throw error;
    }
  };

  const base64ToBlob = (base64, mimeType) => {
    const byteCharacters = atob(base64);
    const byteArrays = [];

    for (let offset = 0; offset < byteCharacters.length; offset += 512) {
      const slice = byteCharacters.slice(offset, offset + 512);
      const byteNumbers = new Array(slice.length);
      
      for (let i = 0; i < slice.length; i++) {
        byteNumbers[i] = slice.charCodeAt(i);
      }
      
      const byteArray = new Uint8Array(byteNumbers);
      byteArrays.push(byteArray);
    }
    
    return new Blob(byteArrays, { type: mimeType });
  };

  const handleGenerateImage = async () => {
    if (!imagePrompt.trim()) {
      setError('Please enter a prompt for image generation');
      return;
    }

    setIsGeneratingImage(true);
    setError('');

    try {
      const imageUrl = await generateImageWithStableDiffusion(imagePrompt);
      setGeneratedImage(imageUrl);
    } catch (err) {
      setError(err.message || 'Failed to generate image. Please try again.');
      // Fallback to mock image for demo purposes
      setGeneratedImage('https://images.unsplash.com/photo-1611224923853-80b023f02d71?w=512&h=512&fit=crop');
    } finally {
      setIsGeneratingImage(false);
    }
  };

  const handleRegenerateImage = async () => {
    if (imagePrompt.trim()) {
      await handleGenerateImage();
    }
  };

  const handleDownloadImage = () => {
    if (generatedImage) {
      const link = document.createElement('a');
      link.href = generatedImage;
      link.download = `vericreate-image-${Date.now()}.png`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    }
  };

  const handleGenerateContent = async () => {
    if (!title.trim()) return;
    
    setIsGenerating(true);
    setError('');

    try {
      // Generate content
      const content = {
        title,
        language,
        content: `This is AI-generated content for "${title}" in ${language}. It includes engaging copy and relevant hashtags optimized for social media platforms.`,
        image: generatedImage || 'https://images.unsplash.com/photo-1611224923853-80b023f02d71?w=400&h=300&fit=crop',
        timestamp: new Date().toISOString(),
        hash: generateHash(title + language + Date.now())
      };

      onGenerate(content);
    } catch (err) {
      setError('Failed to generate content: ' + err.message);
    } finally {
      setIsGenerating(false);
    }
  };

  const generateHash = (text) => {
    return '0x' + Array.from(text)
      .reduce((hash, char) => (hash << 5) - hash + char.charCodeAt(0), 0)
      .toString(16)
      .slice(0, 64);
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      style={cardStyles.container}
    >
      <h2 style={cardStyles.heading}>
        <Wand2 size={28} color={colors.primary} />
        AI Content Generation
      </h2>

      {error && (
        <motion.div
          initial={{ opacity: 0, height: 0 }}
          animate={{ opacity: 1, height: 'auto' }}
          style={cardStyles.error}
        >
          <AlertCircle size={18} />
          {error}
        </motion.div>
      )}

      <div style={cardStyles.inputGroup}>
        {/* Content Title */}
        <div>
          <label style={cardStyles.label}>Content Title *</label>
          <motion.input
            whileFocus={{ scale: 1.02 }}
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Enter your content topic or headline..."
            style={cardStyles.input}
          />
        </div>

        {/* Image Generation Section */}
        <div>
          <label style={cardStyles.label}>
            <ImageIcon size={16} style={{ marginRight: '0.5rem' }} />
            AI Image Generation (Optional)
          </label>
          <textarea
            value={imagePrompt}
            onChange={(e) => setImagePrompt(e.target.value)}
            placeholder="Describe the image you want to generate... (e.g., 'modern abstract background with tech elements')"
            style={cardStyles.textarea}
          />
          
          <motion.button
            onClick={handleGenerateImage}
            disabled={isGeneratingImage || !imagePrompt.trim()}
            whileHover={{ scale: isGeneratingImage || !imagePrompt.trim() ? 1 : 1.02 }}
            whileTap={{ scale: 0.98 }}
            style={{
              ...cardStyles.imageButton,
              background: isGeneratingImage || !imagePrompt.trim() ? colors.border : colors.surface,
              cursor: isGeneratingImage || !imagePrompt.trim() ? 'not-allowed' : 'pointer',
              opacity: isGeneratingImage || !imagePrompt.trim() ? 0.7 : 1,
            }}
          >
            <AnimatePresence mode="wait">
              {isGeneratingImage ? (
                <motion.div
                  key="loading"
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  exit={{ opacity: 0 }}
                  style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}
                >
                  <motion.div
                    animate={{ rotate: 360 }}
                    transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
                    style={{ width: '18px', height: '18px', border: '2px solid currentColor', borderTop: '2px solid transparent', borderRadius: '50%' }}
                  />
                  Generating Image...
                </motion.div>
              ) : (
                <motion.div
                  key="generate"
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  exit={{ opacity: 0 }}
                  style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}
                >
                  <Wand2 size={18} />
                  Generate AI Image
                </motion.div>
              )}
            </AnimatePresence>
          </motion.button>

          {/* Generated Image Preview */}
          {generatedImage && (
            <motion.div
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: 0.5 }}
            >
              <img 
                src={generatedImage} 
                alt="AI Generated" 
                style={cardStyles.imagePreview}
              />
              
              {/* Image Actions */}
              <div style={cardStyles.imageActions}>
                <motion.button
                  onClick={handleRegenerateImage}
                  disabled={isGeneratingImage}
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  style={cardStyles.actionButton}
                >
                  <RefreshCw size={16} />
                  Regenerate
                </motion.button>
                
                <motion.button
                  onClick={handleDownloadImage}
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  style={cardStyles.actionButton}
                >
                  <Download size={16} />
                  Download
                </motion.button>
              </div>
            </motion.div>
          )}
        </div>

        {/* Language Selection */}
        <div>
          <label style={{ ...cardStyles.label, display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
            <Languages size={16} />
            Language
          </label>
          <motion.select
            whileFocus={{ scale: 1.02 }}
            value={language}
            onChange={(e) => setLanguage(e.target.value)}
            style={cardStyles.select}
          >
            {languages.map((lang) => (
              <option key={lang} value={lang} style={{ background: colors.surface, color: colors.text }}>
                {lang}
              </option>
            ))}
          </motion.select>
        </div>
      </div>

      {/* Generate Content Button */}
      <motion.button
        onClick={handleGenerateContent}
        disabled={!title.trim() || isGenerating}
        whileHover={{ scale: !title.trim() || isGenerating ? 1 : 1.05, y: -2 }}
        whileTap={{ scale: 0.95 }}
        style={{
          ...cardStyles.button,
          background: !title.trim() || isGenerating ? colors.border : colors.gradient,
          cursor: !title.trim() || isGenerating ? 'not-allowed' : 'pointer',
          boxShadow: !title.trim() || isGenerating ? 'none' : `0 8px 32px ${colors.primary}40`,
        }}
      >
        <AnimatePresence mode="wait">
          {isGenerating ? (
            <motion.div
              key="loading"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              style={{ display: 'flex', alignItems: 'center', gap: '0.75rem' }}
            >
              <motion.div
                animate={{ rotate: 360 }}
                transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
                style={{ width: '20px', height: '20px', border: '2px solid white', borderTop: '2px solid transparent', borderRadius: '50%' }}
              />
              Generating Content...
            </motion.div>
          ) : (
            <motion.div
              key="generate"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              style={{ display: 'flex', alignItems: 'center', gap: '0.75rem' }}
            >
              <Wand2 size={20} />
              Generate Complete Content
            </motion.div>
          )}
        </AnimatePresence>
      </motion.button>

      {/* API Key Note */}
      <motion.p
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.5 }}
        style={{ 
          color: colors.textSecondary, 
          fontSize: '0.8rem', 
          textAlign: 'center',
          marginTop: '1rem'
        }}
      >
        Powered by Stable Diffusion AI. Images are generated in real-time using advanced AI models.
      </motion.p>
    </motion.div>
  );
};

export default GenerateCard;
