// HeroSection.js - Enhanced Home Page
import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import { useTheme } from '../App';
import { 
  Brain, Zap, Shield, Globe, TrendingUp, Users, Target, Clock, 
  Play, Star, CheckCircle, BarChart3, FileText, Video, Mail, 
  Cloud, Lock, Award, Rocket, Cpu, Database, Server, Code, 
  Palette, ShieldCheck, Globe2, Smartphone, Headphones, 
  MessageCircle, ThumbsUp, Eye, Download, ArrowRight,
  Calendar, PieChart, Settings, Wifi, WifiOff
} from 'lucide-react';

const HeroSection = () => {
  const { colors } = useTheme();
  const [isOnline, setIsOnline] = useState(true);

  useEffect(() => {
    setIsOnline(navigator.onLine);
    window.addEventListener('online', () => setIsOnline(true));
    window.addEventListener('offline', () => setIsOnline(false));
  }, []);

  const heroStyles = {
    container: {
      position: 'relative',
      minHeight: '100vh',
      display: 'flex',
      alignItems: 'center',
      padding: '120px 2rem 2rem 2rem',
      overflow: 'hidden',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
    },
    content: {
      position: 'relative',
      zIndex: 10,
      maxWidth: '1400px',
      margin: '0 auto',
      width: '100%',
    },
    grid: {
      display: 'grid',
      gridTemplateColumns: '1fr',
      gap: '4rem',
      alignItems: 'center',
    },
    textContent: {
      textAlign: 'center',
    },
    badge: {
      display: 'inline-flex',
      alignItems: 'center',
      gap: '0.5rem',
      background: 'rgba(255, 255, 255, 0.15)',
      color: 'white',
      padding: '0.75rem 1.5rem',
      borderRadius: '50px',
      fontSize: '0.875rem',
      fontWeight: '600',
      marginBottom: '2rem',
      backdropFilter: 'blur(10px)',
      border: '1px solid rgba(255, 255, 255, 0.2)',
    },
    heading: {
      fontSize: 'clamp(2.5rem, 5vw, 4rem)',
      fontWeight: 'bold',
      color: 'white',
      lineHeight: 1.1,
      marginBottom: '1.5rem',
      textShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    },
    highlight: {
      background: 'linear-gradient(45deg, #FFD700, #FFA500)',
      WebkitBackgroundClip: 'text',
      WebkitTextFillColor: 'transparent',
      backgroundClip: 'text',
    },
    subheading: {
      fontSize: 'clamp(1rem, 2vw, 1.25rem)',
      color: 'rgba(255, 255, 255, 0.9)',
      lineHeight: 1.6,
      marginBottom: '2.5rem',
      maxWidth: '800px',
      margin: '0 auto',
    },
    buttonGroup: {
      display: 'flex',
      gap: '1rem',
      justifyContent: 'center',
      flexWrap: 'wrap',
      marginBottom: '4rem',
    },
    primaryButton: {
      background: 'linear-gradient(45deg, #FF6B6B, #FFA500)',
      color: 'white',
      padding: '1.25rem 2.5rem',
      borderRadius: '16px',
      fontWeight: '600',
      fontSize: '1.1rem',
      textDecoration: 'none',
      border: 'none',
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      gap: '0.75rem',
      transition: 'all 0.3s ease',
      boxShadow: '0 8px 32px rgba(255, 107, 107, 0.4)',
    },
    secondaryButton: {
      background: 'rgba(255, 255, 255, 0.1)',
      color: 'white',
      padding: '1.25rem 2.5rem',
      borderRadius: '16px',
      fontWeight: '600',
      fontSize: '1.1rem',
      textDecoration: 'none',
      border: '2px solid rgba(255, 255, 255, 0.3)',
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      gap: '0.75rem',
      transition: 'all 0.3s ease',
      backdropFilter: 'blur(10px)',
    },
    tertiaryButton: {
      background: 'transparent',
      color: 'white',
      padding: '1rem 2rem',
      borderRadius: '12px',
      fontWeight: '500',
      fontSize: '1rem',
      textDecoration: 'none',
      border: '1px solid rgba(255, 255, 255, 0.2)',
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem',
      transition: 'all 0.3s ease',
    },
    problemSection: {
      background: 'rgba(255, 255, 255, 0.1)',
      backdropFilter: 'blur(10px)',
      border: '1px solid rgba(255, 255, 255, 0.2)',
      borderRadius: '20px',
      padding: '3rem 2rem',
      marginBottom: '3rem',
    },
    problemTitle: {
      color: 'white',
      fontSize: '1.5rem',
      fontWeight: '600',
      marginBottom: '2rem',
      textAlign: 'center',
    },
    problemGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
      gap: '1.5rem',
    },
    problemCard: {
      background: 'rgba(255, 255, 255, 0.05)',
      padding: '1.5rem',
      borderRadius: '12px',
      border: '1px solid rgba(255, 255, 255, 0.1)',
      textAlign: 'center',
    },
    problemIcon: {
      fontSize: '2rem',
      marginBottom: '1rem',
    },
    problemText: {
      color: 'rgba(255, 255, 255, 0.9)',
      fontSize: '0.9rem',
    },
    featuresGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
      gap: '2rem',
      marginTop: '3rem',
    },
    featureCard: {
      background: 'rgba(255, 255, 255, 0.1)',
      backdropFilter: 'blur(10px)',
      padding: '2.5rem 2rem',
      borderRadius: '20px',
      border: '1px solid rgba(255, 255, 255, 0.2)',
      textAlign: 'center',
      transition: 'all 0.3s ease',
    },
    statsGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))',
      gap: '2rem',
      marginTop: '4rem',
      padding: '2rem',
      background: 'rgba(255, 255, 255, 0.1)',
      backdropFilter: 'blur(10px)',
      borderRadius: '20px',
      border: '1px solid rgba(255, 255, 255, 0.2)',
    },
    statItem: {
      textAlign: 'center',
    },
    statNumber: {
      fontSize: '2.5rem',
      fontWeight: 'bold',
      background: 'linear-gradient(45deg, #FFD700, #FFA500)',
      WebkitBackgroundClip: 'text',
      WebkitTextFillColor: 'transparent',
      backgroundClip: 'text',
      marginBottom: '0.5rem',
    },
    statLabel: {
      color: 'rgba(255, 255, 255, 0.8)',
      fontSize: '1rem',
      fontWeight: '500',
    },
    trustedBy: {
      textAlign: 'center',
      marginTop: '4rem',
      padding: '2rem',
    },
    trustedTitle: {
      color: 'rgba(255, 255, 255, 0.8)',
      fontSize: '1rem',
      fontWeight: '500',
      marginBottom: '1.5rem',
    },
    logos: {
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      gap: '3rem',
      flexWrap: 'wrap',
    },
    logoItem: {
      color: 'rgba(255, 255, 255, 0.7)',
      fontSize: '1.25rem',
      fontWeight: '600',
      opacity: 0.7,
    },
    industrySection: {
      background: 'rgba(255, 255, 255, 0.05)',
      padding: '4rem 2rem',
      borderRadius: '20px',
      marginTop: '3rem',
    },
    industryTitle: {
      color: 'white',
      fontSize: '2rem',
      fontWeight: 'bold',
      textAlign: 'center',
      marginBottom: '3rem',
    },
    industryGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))',
      gap: '2rem',
    },
    industryCard: {
      background: 'rgba(255, 255, 255, 0.1)',
      padding: '2rem',
      borderRadius: '16px',
      border: '1px solid rgba(255, 255, 255, 0.2)',
      textAlign: 'center',
    },
    uspSection: {
      background: 'rgba(255, 255, 255, 0.05)',
      padding: '4rem 2rem',
      borderRadius: '20px',
      marginTop: '3rem',
    },
    uspGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
      gap: '2rem',
    },
    uspCard: {
      background: 'rgba(255, 255, 255, 0.1)',
      padding: '2rem',
      borderRadius: '16px',
      border: '1px solid rgba(255, 255, 255, 0.2)',
      textAlign: 'center',
    },
    testimonialSection: {
      background: 'rgba(255, 255, 255, 0.1)',
      padding: '4rem 2rem',
      borderRadius: '20px',
      marginTop: '3rem',
    },
    testimonialGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
      gap: '2rem',
    },
    testimonialCard: {
