// HeroSection.js - Enhanced Home Page
import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import { useTheme } from '../App';
import { 
  Brain, Zap, Shield, Globe, TrendingUp, Users, Target, Clock, 
  Play, Star, CheckCircle, BarChart3, FileText, Video, Mail, 
  Cloud, Lock, Award, Rocket, Cpu, Database, Server, Eye,
  MessageCircle, Calendar, ShieldCheck, Languages, BarChart,
  Download, ArrowRight, ChevronDown, ThumbsUp, Heart
} from 'lucide-react';

const HeroSection = () => {
  const { colors } = useTheme();
  const [currentTestimonial, setCurrentTestimonial] = useState(0);

  const testimonials = [
    {
      name: "Sarah Chen",
      role: "Content Director, CNN Media",
      content: "Reduced our content production time by 85% while maintaining quality. Game-changing platform!",
      avatar: "üë©‚Äçüíº"
    },
    {
      name: "Marcus Rodriguez",
      role: "CEO, EduTech Global",
      content: "The multi-language support helped us expand to 3 new markets in just 2 months.",
      avatar: "üë®‚Äçüíº"
    },
    {
      name: "Emily Watson",
      role: "Marketing Head, OmniAdvertising",
      content: "Blockchain verification gives our clients unprecedented trust in content authenticity.",
      avatar: "üë©‚Äçüéì"
    }
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTestimonial((prev) => (prev + 1) % testimonials.length);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  const heroStyles = {
    container: {
      position: 'relative',
      minHeight: '100vh',
      display: 'flex',
      alignItems: 'center',
      padding: '120px 2rem 2rem 2rem',
      overflow: 'hidden',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
    },
    content: {
      position: 'relative',
      zIndex: 10,
      maxWidth: '1400px',
      margin: '0 auto',
      width: '100%',
    },
    // ... (previous styles remain the same, adding new ones below)
    testimonialSection: {
      background: 'rgba(255, 255, 255, 0.1)',
      backdropFilter: 'blur(10px)',
      border: '1px solid rgba(255, 255, 255, 0.2)',
      borderRadius: '20px',
      padding: '2.5rem',
      marginTop: '3rem',
      position: 'relative',
      overflow: 'hidden',
    },
    testimonialContent: {
      textAlign: 'center',
      color: 'white',
    },
    testimonialAvatar: {
      fontSize: '3rem',
      marginBottom: '1rem',
    },
    testimonialText: {
      fontSize: '1.25rem',
      fontStyle: 'italic',
      marginBottom: '1.5rem',
      lineHeight: 1.6,
    },
    testimonialAuthor: {
      fontWeight: '600',
      fontSize: '1.1rem',
    },
    testimonialRole: {
      opacity: 0.8,
      fontSize: '0.9rem',
    },
    ctaSection: {
      background: 'linear-gradient(45deg, rgba(255, 107, 107, 0.1), rgba(255, 165, 0, 0.1))',
      border: '1px solid rgba(255, 255, 255, 0.2)',
      borderRadius: '20px',
      padding: '4rem 2rem',
      marginTop: '4rem',
      textAlign: 'center',
    },
    ctaTitle: {
      color: 'white',
      fontSize: '2.5rem',
      fontWeight: 'bold',
      marginBottom: '1rem',
    },
    ctaSubtitle: {
      color: 'rgba(255, 255, 255, 0.9)',
      fontSize: '1.2rem',
      marginBottom: '2.5rem',
    },
    scrollIndicator: {
      position: 'absolute',
      bottom: '2rem',
      left: '50%',
      transform: 'translateX(-50%)',
      color: 'rgba(255, 255, 255, 0.7)',
      textAlign: 'center',
      animation: 'bounce 2s infinite',
    },
    uspGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))',
      gap: '1.5rem',
      marginTop: '3rem',
    },
    uspCard: {
      background: 'rgba(255, 255, 255, 0.05)',
      padding: '1.5rem',
      borderRadius: '12px',
      border: '1px solid rgba(255, 255, 255, 0.1)',
      display: 'flex',
      alignItems: 'center',
      gap: '1rem',
    },
    integrationGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
      gap: '1.5rem',
      marginTop: '2rem',
    },
    integrationCard: {
      background: 'rgba(255, 255, 255, 0.05)',
      padding: '1.5rem',
      borderRadius: '12px',
      border: '1px solid rgba(255, 255, 255, 0.1)',
      textAlign: 'center',
      transition: 'all 0.3s ease',
    },
  };

  const problems = [
    { icon: '‚è∞', text: 'Content bottlenecks slowing down your campaigns' },
    { icon: 'üî•', text: 'Team burnout from manual content creation' },
    { icon: 'üåç', text: 'Struggling with multi-language content demands' },
    { icon: 'üìà', text: 'Inability to scale content production efficiently' },
    { icon: 'üí∞', text: 'High costs of traditional content creation' },
    { icon: 'üîÑ', text: 'Difficulty maintaining consistent brand voice' },
  ];

  const features = [
    {
      icon: Zap,
      title: '10x Faster Content Creation',
      description: 'Generate high-quality content in seconds instead of hours with AI-powered automation'
    },
    {
      icon: Shield,
      title: 'Blockchain Content Verification',
      description: 'Prove authenticity and ownership with immutable timestamps on Ethereum and Aptos'
    },
    {
      icon: Globe,
      title: '50+ Language Support',
      description: 'Automatically create and optimize content for global audiences'
    },
    {
      icon: Users,
      title: 'Enterprise Team Collaboration',
      description: 'Seamless workflow management for distributed content teams'
    },
    {
      icon: Cpu,
      title: 'Advanced AI Models',
      description: 'Multiple AI engines optimized for different content types and industries'
    },
    {
      icon: Database,
      title: 'Smart Content Repository',
      description: 'AI-powered content library with smart tagging and instant retrieval'
    },
  ];

  // NEW USPs
  const additionalUSPs = [
    {
      icon: BarChart,
      title: 'Real-time Performance Analytics',
      description: 'Track content performance with detailed insights and optimization suggestions'
    },
    {
      icon: Calendar,
      title: 'Automated Content Scheduling',
      description: 'Plan and schedule content across all platforms from one dashboard'
    },
    {
      icon: ShieldCheck,
      title: 'Enterprise-grade Security',
      description: 'SOC 2 compliant with end-to-end encryption and role-based access'
    },
    {
      icon: MessageCircle,
      title: 'Multi-channel Distribution',
      description: 'Publish seamlessly to websites, social media, email, and more'
    },
    {
      icon: Languages,
      title: 'Cultural Adaptation AI',
      description: 'Automatically adapt content for cultural nuances and local preferences'
    },
    {
      icon: Server,
      title: '99.9% Uptime Guarantee',
      description: 'Enterprise reliability with SLA-backed performance guarantees'
    },
  ];

  const industries = [
    {
      icon: 'üì∞',
      title: 'Media & Publishing',
      description: 'Automate news articles, social posts, and engagement content'
    },
    {
      icon: 'üéì',
      title: 'Education & EdTech',
      description: 'Create learning materials, course content, and educational resources'
    },
    {
      icon: 'üì¢',
      title: 'Advertising & Marketing',
      description: 'Generate campaign content, ad copy, and marketing materials'
    },
    {
      icon: 'üèõ',
      title: 'Public Outreach',
      description: 'Produce government communications and public service content'
    },
    {
      icon: 'üè•',
      title: 'Healthcare',
      description: 'Create patient education materials and medical content'
    },
    {
      icon: 'üõí',
      title: 'E-commerce',
      description: 'Generate product descriptions, reviews, and marketing content'
    },
  ];

  const stats = [
    { number: '10x', label: 'Faster Creation', icon: Zap },
    { number: '50+', label: 'Languages', icon: Globe },
    { number: '99.9%', label: 'Uptime', icon: Server },
    { number: '24/7', label: 'AI Support', icon: Cpu },
    { number: '500+', label: 'Templates', icon: FileText },
    { number: '1M+', label: 'Content Pieces', icon: Database },
  ];

  const trustedCompanies = ['CNN Media', 'EduTech Global', 'OmniAdvertising', 'PublicOutreach Inc', 'LearnSphere', 'HealthCare Plus', 'ShopGlobal', 'TechInnovate'];

  const integrations = [
    { name: 'WordPress', icon: 'üîó' },
    { name: 'Shopify', icon: 'üõí' },
    { name: 'Mailchimp', icon: 'üìß' },
    { name: 'Slack', icon: 'üí¨' },
    { name: 'Google Drive', icon: '‚òÅ' },
    { name: 'Meta', icon: 'üì±' },
  ];

  return (
    <div style={heroStyles.container}>
      <div style={heroStyles.content}>
        <motion.div
          initial="hidden"
          animate="visible"
          variants={{
            hidden: { opacity: 0 },
            visible: {
              opacity: 1,
              transition: {
                staggerChildren: 0.2
              }
            }
          }}
          style={heroStyles.grid}
        >
          <div style={heroStyles.textContent}>
            {/* Main Hero Section */}
            <motion.div
              initial={{ y: 30, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ duration: 0.6 }}
            >
              <div style={heroStyles.badge}>
                <Rocket size={18} />
                Enterprise AI Content Platform - Trusted by 10,000+ Teams
              </div>
            </motion.div>

            <motion.h1 
              initial={{ y: 30, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ duration: 0.6, delay: 0.2 }}
              style={heroStyles.heading}
            >
              End Content Bottlenecks with{' '}
              <span style={heroStyles.highlight}>AI-Powered Automation</span>
            </motion.h1>

            <motion.p 
              initial={{ y: 30, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ duration: 0.6, delay: 0.4 }}
              style={heroStyles.subheading}
            >
              Transform your content pipeline from overwhelmed to overachieving. 
              Automate high-volume content production across media, education, advertising, 
              and public outreach with enterprise-grade AI and blockchain verification.
            </motion.p>

            {/* Enhanced Problem Statement Section */}
            <motion.div 
              initial={{ y: 30, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ duration: 0.6, delay: 0.6 }}
              style={heroStyles.problemSection}
            >
              <h3 style={heroStyles.problemTitle}>Are You Struggling With These Content Challenges?</h3>
              <div style={heroStyles.problemGrid}>
                {problems.map((problem, index) => (
                  <motion.div
                    key={index}
                    whileHover={{ scale: 1.05, y: -5 }}
                    style={heroStyles.problemCard}
                  >
                    <div style={heroStyles.problemIcon}>{problem.icon}</div>
                    <p style={heroStyles.problemText}>{problem.text}</p>
                  </motion.div>
                ))}
              </div>
            </motion.div>

            <motion.div 
              initial={{ y: 30, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ duration: 0.6, delay: 0.8 }}
              style={heroStyles.buttonGroup}
            >
              <Link to="/dashboard">
                <motion.button
                  whileHover={{ scale: 1.05, y: -2 }}
                  whileTap={{ scale: 0.95 }}
                  style={heroStyles.primaryButton}
                >
                  <Brain size={22} />
                  Start Creating Free - No Credit Card
                </motion.button>
              </Link>
              <motion.button
                whileHover={{ scale: 1.05, y: -2 }}
                whileTap={{ scale: 0.95 }}
                style={heroStyles.secondaryButton}
              >
                <Play size={22} />
                Watch 2-min Demo
              </motion.button>
              <motion.button
                whileHover={{ scale: 1.05, y: -2 }}
                whileTap={{ scale: 0.95 }}
                style={{...heroStyles.secondaryButton, background: 'rgba(255, 255, 255, 0.05)'}}
              >
                <Download size={22} />
                Case Studies
              </motion.button>
            </motion.div>

            {/* Enhanced Stats with Icons */}
            <motion.div 
              initial={{ y: 30, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ duration: 0.6, delay: 1 }}
              style={heroStyles.statsGrid}
            >
              {stats.map((stat, index) => (
                <motion.div 
                  key={index} 
                  style={heroStyles.statItem}
                  whileHover={{ scale: 1.05 }}
                >
                  <stat.icon 
                    size={32} 
                    style={{ 
                      color: '#FFD700',
                      margin: '0 auto 1rem'
                    }} 
                  />
                  <div style={heroStyles.statNumber}>{stat.number}</div>
                  <div style={heroStyles.statLabel}>{stat.label}</div>
                </motion.div>
              ))}
            </motion.div>

            {/* Additional USPs Section */}
            <motion.div 
              initial={{ y: 30, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ duration: 0.6, delay: 1.1 }}
            >
              <h3 style={{
                color: 'white',
                textAlign: 'center',
                fontSize: '2rem',
                fontWeight: 'bold',
                marginBottom: '2rem'
              }}>
                Why Choose Our Platform?
              </h3>
              <div style={heroStyles.uspGrid}>
                {additionalUSPs.map((usp, index) => (
                  <motion.div
                    key={index}
                    whileHover={{ y: -5, background: 'rgba(255, 255, 255, 0.1)' }}
                    style={heroStyles.uspCard}
                  >
                    <usp.icon size={24} style={{ color: '#FFD700', flexShrink: 0 }} />
                    <div>
                      <h4 style={{ color: 'white', margin: '0 0 0.5rem 0', fontSize: '1rem' }}>
                        {usp.title}
                      </h4>
                      <p style={{ color: 'rgba(255, 255, 255, 0.8)', fontSize: '0.9rem', margin: 0 }}>
                        {usp.description}
                      </p>
                    </div>
                  </motion.div>
                ))}
              </div>
            </motion.div>

            {/* Features Grid */}
            <motion.div 
              initial={{ y: 30, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ duration: 0.6, delay: 1.2 }}
              style={heroStyles.featuresGrid}
            >
              {features.map((feature, index) => (
                <motion.div
                  key={index}
                  whileHover={{ y: -8, scale: 1.02 }}
                  style={heroStyles.featureCard}
                >
                  <feature.icon 
                    size={48} 
                    style={{ 
                      color: '#FFD700',
                      margin: '0 auto 1.5rem'
                    }} 
                  />
                  <h3 style={{ 
                    color: 'white', 
                    marginBottom: '1rem', 
                    fontSize: '1.5rem', 
                    fontWeight: '600' 
                  }}>
                    {feature.title}
                  </h3>
                  <p style={{ 
                    color: 'rgba(255, 255, 255, 0.8)', 
                    lineHeight: 1.6,
                    fontSize: '1rem'
                  }}>
                    {feature.description}
                  </p>
                </motion.div>
              ))}
            </motion.div>

            {/* Testimonials Section */}
            <motion.div 
              initial={{ y: 30, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ duration: 0.6, delay: 1.4 }}
              style={heroStyles.testimonialSection}
            >
              <motion.div
                key={currentTestimonial}
                initial={{ opacity: 0, x: 50 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.5 }}
                style={heroStyles.testimonialContent}
              >
                <div style={heroStyles.testimonialAvatar}>
                  {testimonials[currentTestimonial].avatar}
                </div>
                <p style={heroStyles.testimonialText}>
                  "{testimonials[currentTestimonial].content}"
                </p>
                <div style={heroStyles.testimonialAuthor}>
                  {testimonials[currentTestimonial].name}
                </div>
                <div style={heroStyles.testimonialRole}>
                  {testimonials[currentTestimonial].role}
                </div>
              </motion.div>
            </motion.div>

            {/* Integrations Section */}
            <motion.div 
              initial={{ y: 30, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ duration: 0.6, delay: 1.6 }}
            >
              <h3 style={{
                color: 'white',
                textAlign: 'center',
                fontSize: '1.5rem',
                fontWeight: '600',
                marginBottom: '2rem',
                marginTop: '4rem'
              }}>
                Seamless Integrations
              </h3>
              <div style={heroStyles.integrationGrid}>
                {integrations.map((integration, index) => (
                  <motion.div
                    key={index}
                    whileHover={{ scale: 1.05, y: -3 }}
                    style={heroStyles.integrationCard}
                  >
                    <div style={{ fontSize: '2rem', marginBottom: '0.5rem' }}>
                      {integration.icon}
                    </div>
                    <div style={{ color: 'white', fontWeight: '500' }}>
                      {integration.name}
                    </div>
                  </motion.div>
                ))}
              </div>
            </motion.div>

            {/* Industry Solutions */}
            <motion.div 
              initial={{ y: 30, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ duration: 0.6, delay: 1.8 }}
              style={heroStyles.industrySection}
            >
              <h2 style={heroStyles.industryTitle}>Trusted by Industry Leaders</h2>
              <div style={heroStyles.industryGrid}>
                {industries.map((industry, index) => (
                  <motion.div
                    key={index}
                    whileHover={{ y: -5, scale: 1.02 }}
                    style={heroStyles.industryCard}
                  >
                    <div style={{ fontSize: '3rem', marginBottom: '1rem' }}>
                      {industry.icon}
                    </div>
                    <h3 style={{ 
                      color: 'white', 
                      fontSize: '1.25rem', 
                      fontWeight: '600',
                      marginBottom: '1rem'
                    }}>
                      {industry.title}
                    </h3>
                    <p style={{ 
                      color: 'rgba(255, 255, 255, 0.8)',
                      lineHeight: 1.5
                    }}>
                      {industry.description}
                    </p>
                  </motion.div>
                ))}
              </div>
            </motion.div>

            {/* Final CTA Section */}
            <motion.div 
              initial={{ y: 30, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ duration: 0.6, delay: 2 }}
              style={heroStyles.ctaSection}
            >
              <h2 style={heroStyles.ctaTitle}>
                Ready to Transform Your Content Strategy?
              </h2>
              <p style={heroStyles.ctaSubtitle}>
                Join 10,000+ teams already creating amazing content faster and smarter
              </p>
              <div style={heroStyles.buttonGroup}>
                <Link to="/signup">
                  <motion.button
                    whileHover={{ scale: 1.05, y: -2 }}
                    whileTap={{ scale: 0.95 }}
                    style={{
                      ...heroStyles.primaryButton,
                      padding: '1.5rem 3rem',
                      fontSize: '1.2rem'
                    }}
                  >
                    <Rocket size={24} />
                    Start Free Trial - 14 Days
                  </motion.button>
                </Link>
                <motion.button
                  whileHover={{ scale: 1.05, y: -2 }}
                  whileTap={{ scale: 0.95 }}
                  style={{
                    ...heroStyles.secondaryButton,
                    padding: '1.5rem 2.5rem',
                    fontSize: '1.2rem'
                  }}
                >
                  <MessageCircle size={24} />
                  Book a Demo
                </motion.button>
              </div>
            </motion.div>

            {/* Trusted By Section */}
            <motion.div 
              initial={{ y: 30, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ duration: 0.6, delay: 2.2 }}
              style={heroStyles.trustedBy}
            >
              <div style={heroStyles.trustedTitle}>
                Trusted by leading organizations worldwide
              </div>
              <div style={heroStyles.logos}>
                {trustedCompanies.map((company, index) => (
                  <motion.div
                    key={index}
                    whileHover={{ scale: 1.1, y: -2 }}
                    style={heroStyles.logoItem}
                  >
                    {company}
                  </motion.div>
                ))}
              </div>
            </motion.div>

            {/* Scroll Indicator */}
            <motion.div 
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 0.6, delay: 2.4 }}
              style={heroStyles.scrollIndicator}
            >
              <motion.div
                animate={{ y: [0, 10, 0] }}
                transition={{ duration: 2, repeat: Infinity }}
              >
                <ChevronDown size={24} />
                <div style={{ fontSize: '0.9rem', marginTop: '0.5rem' }}>
                  Scroll to discover more
                </div>
              </motion.div>
            </motion.div>
          </div>
        </motion.div>
      </div>
    </div>
  );
};

export default HeroSection;
