import React from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import { Sparkles, Shield, Zap, Globe } from 'lucide-react';

const HeroSection = () => {
  const heroStyles = {
    container: {
      position: 'relative',
      minHeight: '100vh',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      overflow: 'hidden',
    },
    backgroundBlobs: {
      position: 'absolute',
      inset: 0,
    },
    blob: {
      position: 'absolute',
      borderRadius: '50%',
      mixBlendMode: 'multiply',
      filter: 'blur(40px)',
      opacity: 0.2,
    },
    content: {
      position: 'relative',
      zIndex: 10,
      textAlign: 'center',
      padding: '0 1rem',
    },
    heading: {
      fontSize: '3rem',
      fontWeight: 'bold',
      color: 'white',
      marginBottom: '1.5rem',
      lineHeight: 1.2,
    },
    subheading: {
      fontSize: '1.5rem',
      color: '#e5e7eb',
      marginBottom: '2rem',
      maxWidth: '48rem',
      marginLeft: 'auto',
      marginRight: 'auto',
    },
    buttonGroup: {
      display: 'flex',
      flexDirection: 'column',
      gap: '1rem',
      justifyContent: 'center',
      alignItems: 'center',
      marginBottom: '4rem',
    },
    primaryButton: {
      background: 'linear-gradient(45deg, #8b5cf6, #ec4899)',
      color: 'white',
      padding: '1rem 2rem',
      borderRadius: '9999px',
      fontWeight: 600,
      fontSize: '1.125rem',
      textDecoration: 'none',
      border: 'none',
      cursor: 'pointer',
      boxShadow: '0 10px 25px rgba(139, 92, 246, 0.3)',
      transition: 'all 0.3s ease',
    },
    secondaryButton: {
      background: 'rgba(255, 255, 255, 0.1)',
      backdropFilter: 'blur(10px)',
      color: 'white',
      padding: '1rem 2rem',
      borderRadius: '9999px',
      fontWeight: 600,
      fontSize: '1.125rem',
      textDecoration: 'none',
      border: '1px solid rgba(255, 255, 255, 0.2)',
      cursor: 'pointer',
      transition: 'all 0.3s ease',
    },
    featuresGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(1, 1fr)',
      gap: '1.5rem',
      maxWidth: '64rem',
      margin: '0 auto',
    },
    featureCard: {
      background: 'rgba(255, 255, 255, 0.1)',
      backdropFilter: 'blur(10px)',
      padding: '1.5rem',
      borderRadius: '1rem',
      border: '1px solid rgba(255, 255, 255, 0.2)',
      textAlign: 'center',
      color: 'white',
    },
    scrollIndicator: {
      position: 'absolute',
      bottom: '2rem',
      left: '50%',
      transform: 'translateX(-50%)',
    },
  };

  const floatingIcons = [
    { Icon: Sparkles, delay: 0 },
    { Icon: Shield, delay: 0.5 },
    { Icon: Zap, delay: 1 },
    { Icon: Globe, delay: 1.5 },
  ];

  const features = [
    { icon: Zap, title: "AI-Powered", desc: "Generate content in seconds with advanced AI" },
    { icon: Shield, title: "Blockchain Verified", desc: "Prove authenticity with cryptographic hashes" },
    { icon: Sparkles, title: "Multi-Platform", desc: "Optimized for all social media platforms" }
  ];

  return (
    <div style={heroStyles.container}>
      {/* Animated Background Blobs */}
      <div style={heroStyles.backgroundBlobs}>
        <motion.div
          animate={{ x: [0, 100, 0], y: [0, -50, 0] }}
          transition={{ duration: 20, repeat: Infinity, ease: "linear" }}
          style={{ ...heroStyles.blob, top: '25%', left: '25%', width: '18rem', height: '18rem', background: '#8b5cf6' }}
        />
        <motion.div
          animate={{ x: [0, -100, 0], y: [0, 50, 0] }}
          transition={{ duration: 25, repeat: Infinity, ease: "linear" }}
          style={{ ...heroStyles.blob, top: '50%', right: '25%', width: '18rem', height: '18rem', background: '#ec4899' }}
        />
        <motion.div
          animate={{ x: [0, 50, 0], y: [0, -100, 0] }}
          transition={{ duration: 30, repeat: Infinity, ease: "linear" }}
          style={{ ...heroStyles.blob, bottom: '25%', left: '50%', width: '18rem', height: '18rem', background: '#3b82f6' }}
        />
      </div>

      {/* Floating Icons */}
      {floatingIcons.map(({ Icon, delay }, index) => (
        <motion.div
          key={index}
          style={{
            position: 'absolute',
            color: 'white',
            opacity: 0.2,
            left: `${20 + index * 20}%`,
            top: `${20 + (index % 3) * 20}%`,
          }}
          animate={{ y: [0, -30, 0] }}
          transition={{ duration: 3, delay, repeat: Infinity, ease: "easeInOut" }}
        >
          <Icon size={40} />
        </motion.div>
      ))}

      {/* Hero Content */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        style={heroStyles.content}
      >
        <motion.h1
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2 }}
          style={heroStyles.heading}
        >
          Create, Verify, and{' '}
          <span style={{ background: 'linear-gradient(45deg, #667eea, #764ba2, #f093fb)', WebkitBackgroundClip: 'text', WebkitTextFillColor: 'transparent' }}>
            Own Your Content
          </span>
          <br />
          <span style={{ fontSize: '2.5rem' }}>â€” Instantly</span>
        </motion.h1>

        <motion.p
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.4 }}
          style={heroStyles.subheading}
        >
          AI-powered content creation with blockchain verification. 
          Generate stunning social media content and prove its authenticity with cryptographic signatures.
        </motion.p>

        <motion.div
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.6 }}
          style={heroStyles.buttonGroup}
        >
          <Link to="/dashboard">
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              style={heroStyles.primaryButton}
              onMouseEnter={(e) => {
                e.target.style.boxShadow = '0 15px 35px rgba(139, 92, 246, 0.4)';
                e.target.style.transform = 'translateY(-2px)';
              }}
              onMouseLeave={(e) => {
                e.target.style.boxShadow = '0 10px 25px rgba(139, 92, 246, 0.3)';
                e.target.style.transform = 'translateY(0)';
              }}
            >
              Get Started Free
            </motion.button>
          </Link>
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            style={heroStyles.secondaryButton}
            onMouseEnter={(e) => {
              e.target.style.background = 'rgba(255, 255, 255, 0.15)';
              e.target.style.borderColor = 'rgba(255, 255, 255, 0.4)';
            }}
            onMouseLeave={(e) => {
              e.target.style.background = 'rgba(255, 255, 255, 0.1)';
              e.target.style.borderColor = 'rgba(255, 255, 255, 0.2)';
            }}
          >
            Watch Demo
          </motion.button>
        </motion.div>

        {/* USP Features */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.8 }}
          style={heroStyles.featuresGrid}
        >
          {features.map((feature, index) => (
            <motion.div
              key={index}
              whileHover={{ y: -5 }}
              style={heroStyles.featureCard}
            >
              <feature.icon size={48} color="#d8b4fe" style={{ margin: '0 auto 1rem' }} />
              <h3 style={{ fontSize: '1.25rem', fontWeight: 600, marginBottom: '0.5rem' }}>{feature.title}</h3>
              <p style={{ color: '#d1d5db' }}>{feature.desc}</p>
            </motion.div>
          ))}
        </motion.div>
      </motion.div>

      {/* Scroll Indicator */}
      <motion.div
        animate={{ y: [0, 10, 0] }}
        transition={{ duration: 2, repeat: Infinity }}
        style={heroStyles.scrollIndicator}
      >
        <div style={{ width: '24px', height: '40px', border: '2px solid white', borderRadius: '9999px', display: 'flex', justifyContent: 'center' }}>
          <motion.div
            animate={{ y: [0, 12, 0] }}
            transition={{ duration: 2, repeat: Infinity }}
            style={{ width: '4px', height: '12px', background: 'white', borderRadius: '9999px', marginTop: '8px' }}
          />
        </div>
      </motion.div>
    </div>
  );
};

export default HeroSection;
