// HeroSection.js - Enhanced Home Page
import React from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import { useTheme } from '../App';
import { Brain, Zap, Shield, Globe, TrendingUp, Users, Target, Clock, Play, Star, CheckCircle, BarChart3, FileText, Video, Mail, Cloud, Lock } from 'lucide-react';

const HeroSection = () => {
  const { colors } = useTheme();

  const heroStyles = {
    container: {
      position: 'relative',
      minHeight: '100vh',
      display: 'flex',
      alignItems: 'center',
      padding: '120px 2rem 2rem 2rem',
      overflow: 'hidden',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
    },
    content: {
      position: 'relative',
      zIndex: 10,
      maxWidth: '1400px',
      margin: '0 auto',
      width: '100%',
    },
    grid: {
      display: 'grid',
      gridTemplateColumns: '1fr',
      gap: '4rem',
      alignItems: 'center',
    },
    textContent: {
      textAlign: 'center',
    },
    badge: {
      display: 'inline-flex',
      alignItems: 'center',
      gap: '0.5rem',
      background: 'rgba(255, 255, 255, 0.15)',
      color: 'white',
      padding: '0.75rem 1.5rem',
      borderRadius: '50px',
      fontSize: '0.875rem',
      fontWeight: '600',
      marginBottom: '2rem',
      backdropFilter: 'blur(10px)',
