    secondaryButton: {
      background: 'rgba(255, 255, 255, 0.1)',
      color: 'white',
      padding: '1.25rem 2.5rem',
      borderRadius: '16px',
      fontWeight: '600',
      fontSize: '1.1rem',
      textDecoration: 'none',
      border: '2px solid rgba(255, 255, 255, 0.3)',
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      gap: '0.75rem',
      transition: 'all 0.3s ease',
      backdropFilter: 'blur(10px)',
    },
    problemSection: {
      background: 'rgba(255, 255, 255, 0.1)',
