    },
    trustedTitle: {
      color: 'rgba(255, 255, 255, 0.8)',
      fontSize: '1rem',
      fontWeight: '500',
      marginBottom: '1.5rem',
    },
    logos: {
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      gap: '3rem',
      flexWrap: 'wrap',
    },
    logoItem: {
      color: 'rgba(255, 255, 255, 0.7)',
      fontSize: '1.25rem',
      fontWeight: '600',
      opacity: 0.7,
    },
    industrySection: {
      background: 'rgba(255, 255, 255, 0.05)',
      padding: '4rem 2rem',
      borderRadius: '20px',
      marginTop: '3rem',
    },
    industryTitle: {
      color: 'white',
      fontSize: '2rem',
      fontWeight: 'bold',
      textAlign: 'center',
      marginBottom: '3rem',
    },
    industryGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))',
      gap: '2rem',
    },
    industryCard: {
      background: 'rgba(255, 255, 255, 0.1)',
      padding: '2rem',
      borderRadius: '16px',
      border: '1px solid rgba(255, 255, 255, 0.2)',
      textAlign: 'center',
    },
