              transition={{ duration: 0.6, delay: 0.6 }}
              style={heroStyles.problemSection}
            >
              <h3 style={heroStyles.problemTitle}>Are You Struggling With These Content Challenges?</h3>
              <div style={heroStyles.problemGrid}>
                {problems.map((problem, index) => (
                  <motion.div
                    key={index}
                    whileHover={{ scale: 1.05 }}
                    style={heroStyles.problemCard}
                  >
                    <div style={heroStyles.problemIcon}>{problem.icon}</div>
                    <p style={heroStyles.problemText}>{problem.text}</p>
                  </motion.div>
                ))}
              </div>
            </motion.div>

            <motion.div 
              initial={{ y: 30, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ duration: 0.6, delay: 0.8 }}
              style={heroStyles.buttonGroup}
