              <Link to="/dashboard">
                <motion.button
                  whileHover={{ scale: 1.05, y: -2 }}
                  whileTap={{ scale: 0.95 }}
                  style={heroStyles.primaryButton}
                >
                  <Brain size={22} />
                  Start Creating Free
                </motion.button>
              </Link>
              <motion.button
                whileHover={{ scale: 1.05, y: -2 }}
                whileTap={{ scale: 0.95 }}
                style={heroStyles.secondaryButton}
              >
                <Play size={22} />
                Watch Demo
              </motion.button>
            </motion.div>

            {/* Stats */}
            <motion.div 
              initial={{ y: 30, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ duration: 0.6, delay: 1 }}
              style={heroStyles.statsGrid}
            >
              {stats.map((stat, index) => (
                <div key={index} style={heroStyles.statItem}>
                  <div style={heroStyles.statNumber}>{stat.number}</div>
                  <div style={heroStyles.statLabel}>{stat.label}</div>
                </div>
              ))}
            </motion.div>

            {/* Features Grid */}
            <motion.div 
              initial={{ y: 30, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ duration: 0.6, delay: 1.2 }}
              style={heroStyles.featuresGrid}
            >
              {features.map((feature, index) => (
                <motion.div
                  key={index}
                  whileHover={{ y: -8, scale: 1.02 }}
                  style={heroStyles.featureCard}
                >
                  <feature.icon 
                    size={48} 
                    style={{ 
                      color: '#FFD700',
                      margin: '0 auto 1.5rem'
                    }} 
                  />
                  <h3 style={{ 
                    color: 'white', 
                    marginBottom: '1rem', 
                    fontSize: '1.5rem', 
                    fontWeight: '600' 
                  }}>
                    {feature.title}
                  </h3>
                  <p style={{ 
                    color: 'rgba(255, 255, 255, 0.8)', 
                    lineHeight: 1.6,
                    fontSize: '1rem'
                  }}>
                    {feature.description}
                  </p>
                </motion.div>
              ))}
            </motion.div>

            {/* Industry Solutions */}
            <motion.div 
              initial={{ y: 30, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ duration: 0.6, delay: 1.4 }}
              style={heroStyles.industrySection}
            >
              <h2 style={heroStyles.industryTitle}>Trusted by Industry Leaders</h2>
              <div style={heroStyles.industryGrid}>
                {industries.map((industry, index) => (
                  <motion.div
                    key={index}
                    whileHover={{ y: -5 }}
                    style={heroStyles.industryCard}
                  >
                    <div style={{ fontSize: '3rem', marginBottom: '1rem' }}>
                      {industry.icon}
                    </div>
                    <h3 style={{ 
                      color: 'white', 
                      fontSize: '1.25rem', 
                      fontWeight: '600',
                      marginBottom: '1rem'
                    }}>
                      {industry.title}
                    </h3>
                    <p style={{ 
                      color: 'rgba(255, 255, 255, 0.8)',
                      lineHeight: 1.5
                    }}>
                      {industry.description}
                    </p>
                  </motion.div>
                ))}
              </div>
            </motion.div>

            {/* Trusted By Section */}
            <motion.div 
              initial={{ y: 30, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ duration: 0.6, delay: 1.6 }}
              style={heroStyles.trustedBy}
            >
              <div style={heroStyles.trustedTitle}>
                Trusted by leading organizations worldwide
              </div>
              <div style={heroStyles.logos}>
                {trustedCompanies.map((company, index) => (
                  <motion.div
                    key={index}
                    whileHover={{ scale: 1.1 }}
                    style={heroStyles.logoItem}
                  >
                    {company}
                  </motion.div>
                ))}
              </div>
            </motion.div>
          </div>
        </motion.div>
      </div>
    </div>
  );
};

export default HeroSection;
