import React from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import { useTheme } from '../App';
import { Brain, Zap, Shield, Globe, TrendingUp, Users, Target, Clock } from 'lucide-react';

const HeroSection = () => {
  const { colors } = useTheme();

  const heroStyles = {
    container: {
      position: 'relative',
      minHeight: '100vh',
      display: 'flex',
      alignItems: 'center',
      padding: '120px 2rem 2rem 2rem',
      overflow: 'hidden',
    },
    background: {
      position: 'absolute',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      background: `radial-gradient(circle at 20% 80%, ${colors.primary}20 0%, transparent 50%),
                  radial-gradient(circle at 80% 20%, ${colors.secondary}20 0%, transparent 50%),
                  radial-gradient(circle at 40% 40%, ${colors.accent}20 0%, transparent 50%)`,
    },
    content: {
      position: 'relative',
      zIndex: 10,
      maxWidth: '1400px',
      margin: '0 auto',
      width: '100%',
    },
    grid: {
      display: 'grid',
      gridTemplateColumns: '1fr',
      gap: '4rem',
      alignItems: 'center',
    },
    textContent: {
      textAlign: 'left',
    },
    badge: {
      display: 'inline-flex',
      alignItems: 'center',
      gap: '0.5rem',
      background: colors.gradient,
      color: 'white',
      padding: '0.5rem 1rem',
      borderRadius: '50px',
      fontSize: '0.875rem',
      fontWeight: '600',
      marginBottom: '2rem',
    },
    heading: {
      fontSize: '3.5rem',
      fontWeight: 'bold',
      color: colors.text,
      lineHeight: 1.1,
      marginBottom: '1.5rem',
    },
    highlight: {
      background: colors.gradient,
      WebkitBackgroundClip: 'text',
      WebkitTextFillColor: 'transparent',
      backgroundClip: 'text',
    },
    subheading: {
      fontSize: '1.25rem',
      color: colors.textSecondary,
      lineHeight: 1.6,
      marginBottom: '2.5rem',
      maxWidth: '600px',
    },
    problemStatement: {
      background: colors.surface,
      padding: '1.5rem',
      borderRadius: '16px',
      border: `1px solid ${colors.border}`,
      marginBottom: '2.5rem',
    },
    problemText: {
      color: colors.textSecondary,
      fontSize: '1rem',
      lineHeight: 1.6,
      fontStyle: 'italic',
    },
    buttonGroup: {
      display: 'flex',
      gap: '1rem',
      flexWrap: 'wrap',
    },
    primaryButton: {
      background: colors.gradient,
      color: 'white',
      padding: '1rem 2rem',
      borderRadius: '12px',
      fontWeight: '600',
      fontSize: '1rem',
      textDecoration: 'none',
      border: 'none',
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem',
      transition: 'all 0.3s ease',
    },
    secondaryButton: {
      background: 'transparent',
      color: colors.text,
      padding: '1rem 2rem',
      borderRadius: '12px',
      fontWeight: '600',
      fontSize: '1rem',
      textDecoration: 'none',
      border: `2px solid ${colors.border}`,
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem',
      transition: 'all 0.3s ease',
    },
    statsGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(2, 1fr)',
      gap: '2rem',
      marginTop: '3rem',
    },
    statItem: {
      textAlign: 'center',
    },
    statNumber: {
      fontSize: '2rem',
      fontWeight: 'bold',
      background: colors.gradient,
      WebkitBackgroundClip: 'text',
      WebkitTextFillColor: 'transparent',
      backgroundClip: 'text',
      marginBottom: '0.5rem',
    },
    statLabel: {
      color: colors.textSecondary,
      fontSize: '0.875rem',
      fontWeight: '500',
    },
  };

  const stats = [
    { number: '10x', label: 'Faster Content Creation' },
    { number: '99%', label: 'Reduction in Errors' },
    { number: '50+', label: 'Languages Supported' },
    { number: '24/7', label: 'Production Capacity' },
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { y: 30, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        duration: 0.6,
        ease: "easeOut"
      }
    }
  };

  return (
    <div style={heroStyles.container}>
      <div style={heroStyles.background} />
      
      <div style={heroStyles.content}>
        <motion.div
          variants={containerVariants}
          initial="hidden"
          animate="visible"
          style={heroStyles.grid}
        >
          <div style={heroStyles.textContent}>
            <motion.div variants={itemVariants}>
              <div style={heroStyles.badge}>
                <Zap size={16} />
                AI-Powered Content Platform
              </div>
            </motion.div>

            <motion.h1 variants={itemVariants} style={heroStyles.heading}>
              End Content Bottlenecks with{' '}
              <span style={heroStyles.highlight}>AI-Driven Automation</span>
            </motion.h1>

            <motion.div variants={itemVariants} style={heroStyles.problemStatement}>
              <p style={heroStyles.problemText}>
                "Organizations in media, education, advertising, and public outreach face overwhelming 
                demand for continuous, high-volume content. Manual creation is slow, error-prone, and 
                incapable of sustaining required velocity, leading to operational burnout and strategic stagnation."
              </p>
            </motion.div>

            <motion.p variants={itemVariants} style={heroStyles.subheading}>
              VeriCreate transforms your content pipeline with AI-powered generation, multi-language support, 
              and blockchain verification. Scale your content production without compromising quality or authenticity.
            </motion.p>

            <motion.div variants={itemVariants} style={heroStyles.buttonGroup}>
              <Link to="/dashboard">
                <motion.button
                  whileHover={{ scale: 1.05, y: -2 }}
                  whileTap={{ scale: 0.95 }}
                  style={heroStyles.primaryButton}
                >
                  <Brain size={20} />
                  Start Creating Free
                </motion.button>
              </Link>
              <motion.button
                whileHover={{ scale: 1.05, y: -2 }}
                whileTap={{ scale: 0.95 }}
                style={heroStyles.secondaryButton}
              >
                <Shield size={20} />
                See Live Demo
              </motion.button>
            </motion.div>

            <motion.div variants={itemVariants} style={heroStyles.statsGrid}>
              {stats.map((stat, index) => (
                <div key={index} style={heroStyles.statItem}>
                  <div style={heroStyles.statNumber}>{stat.number}</div>
                  <div style={heroStyles.statLabel}>{stat.label}</div>
                </div>
              ))}
            </motion.div>
          </div>
        </motion.div>
      </div>
    </div>
  );
};

export default HeroSection;
