import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { User, Wallet, Shield, Download, Upload } from 'lucide-react';

const Profile = () => {
  const [isWalletConnected, setIsWalletConnected] = useState(false);

  const stats = [
    { label: 'Content Generated', value: '47', icon: Download },
    { label: 'Content Signed', value: '32', icon: Shield },
    { label: 'Platforms Used', value: '6', icon: Upload },
  ];

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: -20 }}
      className="min-h-screen pt-20 pb-16"
    >
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2 }}
          className="text-center mb-12"
        >
          <h1 className="text-4xl md:text-6xl font-bold text-white mb-4">
            Your <span className="gradient-text">Profile</span>
          </h1>
          <p className="text-xl text-gray-300">
            Manage your account and connected wallets
          </p>
        </motion.div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Profile Card */}
          <motion.div
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ delay: 0.4 }}
            className="lg:col-span-2 space-y-6"
          >
            {/* User Info */}
            <div className="gradient-border rounded-2xl">
              <div className="glass-morphism rounded-2xl p-8">
                <div className="flex items-center space-x-6 mb-6">
                  <motion.div
                    whileHover={{ scale: 1.1 }}
                    className="w-20 h-20 bg-gradient-to-r from-purple-500 to-pink-500 rounded-full flex items-center justify-center"
                  >
                    <User className="h-10 w-10 text-white" />
                  </motion.div>
                  <div>
                    <h2 className="text-2xl font-bold text-white">Welcome, Astha Singh</h2>
                    <p className="text-gray-300">Premium Member</p>
                  </div>
                </div>

                {/* Stats */}
                <div className="grid grid-cols-3 gap-4">
                  {stats.map((stat, index) => (
                    <motion.div
                      key={stat.label}
                      initial={{ opacity: 0, y: 20 }}
                      animate={{ opacity: 1, y: 0 }}
                      transition={{ delay: 0.6 + index * 0.1 }}
                      whileHover={{ scale: 1.05 }}
                      className="text-center p-4 bg-white/5 rounded-xl border border-white/10"
                    >
                      <stat.icon className="h-8 w-8 text-purple-300 mx-auto mb-2" />
                      <div className="text-2xl font-bold text-white">{stat.value}</div>
                      <div className="text-sm text-gray-300">{stat.label}</div>
                    </motion.div>
                  ))}
                </div>
              </div>
            </div>

            {/* Wallet Section */}
            <div className="gradient-border rounded-2xl">
              <div className="glass-morphism rounded-2xl p-8">
                <h3 className="text-xl font-bold text-white mb-4 flex items-center gap-2">
                  <Wallet className="h-5 w-5" />
                  Wallet Connection
                </h3>
                
                {isWalletConnected ? (
                  <motion.div
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    className="space-y-4"
                  >
                    <div className="bg-green-500/20 border border-green-500/30 rounded-xl p-4">
                      <div className="flex items-center justify-between">
                        <div>
                          <div className="text-green-400 font-semibold">Connected</div>
                          <div className="text-sm text-gray-300 mt-1">
                            0x742d35Cc6634C0532925a3b8D...
                          </div>
                        </div>
                        <motion.button
                          whileHover={{ scale: 1.05 }}
                          whileTap={{ scale: 0.95 }}
                          onClick={() => setIsWalletConnected(false)}
                          className="px-4 py-2 bg-red-500 hover:bg-red-600 rounded-lg text-white text-sm"
                        >
                          Disconnect
                        </motion.button>
                      </div>
                    </div>
                  </motion.div>
                ) : (
                  <motion.button
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    onClick={() => setIsWalletConnected(true)}
                    className="w-full py-4 bg-gradient-to-r from-orange-500 to-yellow-500 hover:from-orange-600 hover:to-yellow-600 rounded-xl font-semibold text-white transition-all duration-300 flex items-center justify-center gap-2"
                  >
                    <Wallet className="h-5 w-5" />
                    Connect MetaMask Wallet
                  </motion.button>
                )}
              </div>
            </div>
          </motion.div>

          {/* Sidebar */}
          <motion.div
            initial={{ opacity: 0, x: 20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ delay: 0.6 }}
            className="space-y-6"
          >
            {/* Quick Actions */}
            <div className="gradient-border rounded-2xl">
              <div className="glass-morphism rounded-2xl p-6">
                <h3 className="text-lg font-bold text-white mb-4">Quick Actions</h3>
                <div className="space-y-3">
                  {[
                    { label: 'Generate New Content', icon: Download },
                    { label: 'View Signed Items', icon: Shield },
                    { label: 'Export History', icon: Upload },
                  ].map((action, index) => (
                    <motion.button
                      key={action.label}
                      whileHover={{ x: 5, backgroundColor: 'rgba(255,255,255,0.1)' }}
                      className="w-full text-left p-3 rounded-lg text-gray-300 hover:text-white transition-all duration-300 flex items-center gap-3"
                    >
                      <action.icon className="h-4 w-4" />
                      {action.label}
                    </motion.button>
                  ))}
                </div>
              </div>
            </div>

            {/* Recent Activity */}
            <div className="gradient-border rounded-2xl">
              <div className="glass-morphism rounded-2xl p-6">
                <h3 className="text-lg font-bold text-white mb-4">Recent Activity</h3>
                <div className="space-y-3">
                  {[
                    { action: 'Generated', item: 'Summer Campaign', time: '2 hours ago' },
                    { action: 'Signed', item: 'Product Launch', time: '1 day ago' },
                    { action: 'Generated', item: 'Weekly Posts', time: '2 days ago' },
                  ].map((activity, index) => (
                    <motion.div
                      key={index}
                      initial={{ opacity: 0, y: 10 }}
                      animate={{ opacity: 1, y: 0 }}
                      transition={{ delay: 0.8 + index * 0.1 }}
                      className="flex items-center justify-between text-sm"
                    >
                      <div>
                        <span className="text-white">{activity.action}</span>
                        <span className="text-gray-400"> {activity.item}</span>
                      </div>
                      <span className="text-gray-500">{activity.time}</span>
                    </motion.div>
                  ))}
                </div>
              </div>
            </div>
          </motion.div>
        </div>
      </div>
    </motion.div>
  );
};

export default Profile;
