import React, { useState } from 'react';
import { motion } from 'framer-motion';
import GenerateCard from '../components/GenerateCard';
import OutputCard from '../components/OutputCard';

const Dashboard = () => {
  const [generatedContent, setGeneratedContent] = useState(null);

  const handleGenerate = (content) => {
    setGeneratedContent(content);
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: -20 }}
      className="min-h-screen pt-20 pb-16"
    >
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2 }}
          className="text-center mb-12"
        >
          <h1 className="text-4xl md:text-6xl font-bold text-white mb-4">
            Content <span className="gradient-text">Dashboard</span>
          </h1>
          <p className="text-xl text-gray-300 max-w-2xl mx-auto">
            Generate AI-powered content and verify its authenticity with blockchain technology
          </p>
        </motion.div>

        {/* Content Grid */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Generate Card */}
          <GenerateCard onGenerate={handleGenerate} />
          
          {/* Output Card */}
          <div className="lg:col-span-1">
            {generatedContent ? (
              <OutputCard content={generatedContent} />
            ) : (
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                className="gradient-border rounded-2xl h-full"
              >
                <div className="glass-morphism rounded-2xl p-8 h-full flex items-center justify-center">
                  <div className="text-center">
                    <div className="w-16 h-16 bg-gradient-to-r from-purple-500 to-pink-500 rounded-full flex items-center justify-center mx-auto mb-4">
                      <motion.div
                        animate={{ rotate: 360 }}
                        transition={{ duration: 2, repeat: Infinity, ease: "linear" }}
                        className="w-8 h-8 border-2 border-white border-t-transparent rounded-full"
                      />
                    </div>
                    <h3 className="text-xl font-semibold text-white mb-2">
                      Ready to Create
                    </h3>
                    <p className="text-gray-300">
                      Generate content to see the output here
                    </p>
                  </div>
                </div>
              </motion.div>
            )}
          </div>
        </div>
      </div>
    </motion.div>
  );
};

export default Dashboard;
